$descriptions = explode('<br />'.PHP_EOL.'<br />'.PHP_EOL, $descriptions);

$projects = array();

//$count_descriptions = count($descriptions);
$count_descriptions = 20;

$tags = array('PHP', 'HTML', 'CSS', 'JS', 'MySQL');
$pictures = glob('../img/portfolio/*.png');

for ($i = 0; $i < $count_descriptions; $i++) {

	$rand_year = rand(2014, 2015);
	$rand_month = sprintf('%1$02d', $rand_year == 2015 ? rand(1, 10) : rand(1, 12));
	$rand_day = sprintf('%1$02d', rand(1, 29));
	$rand_hour = sprintf('%1$02d', rand(0, 23));
	$rand_minute = sprintf('%1$02d', rand(0, 59));
	$rand_second = sprintf('%1$02d', rand(0, 59));

	$rand_date = $rand_year.'-'.$rand_month.'-'.$rand_day.' '.$rand_hour.':'.$rand_minute.':'.$rand_second;

	//echo $rand_date.'<br>';

	$project_description = str_replace(';', '.', $descriptions[$i]);
	$first_space_pos = strpos($project_description, ' ');
	$first_point_pos = strpos($project_description, '.');

	$project_client = substr($project_description, 0, $first_space_pos);
	$project_title = substr($project_description, $first_space_pos + 1, $first_point_pos - $first_space_pos);
	$project_description = substr($project_description, $first_point_pos + 2);

	$project_tags = array_slice($tags, rand(0, count($tags) - 1), rand(0, 3));
	$project_tags = implode(', ', $project_tags);

	//echo '<span style="color: red">'.$project_client.'</span><br>';
	//echo '<span style="color: blue">'.$project_title.'</span><br>';
	//echo '<span style="color: green">'.$project_description.'</span><br><br>';

	$project_picture_key =  rand(-1, count($pictures) - 1);
	$project_picture = '';
	if ($project_picture_key > -1) {
		$project_picture = basename($pictures[$project_picture_key]);
	}

	$projects[] = array(
		'title' => ucfirst($project_title),
		'description' => ucfirst(wordwrap($project_description, 100, PHP_EOL.PHP_EOL)),
		'client' => ucfirst($project_client),
		'tags' => $project_tags,
		'picture' => $project_picture,
		'date' => $rand_date
	);
}

$query = $db->prepare('INSERT INTO projects (title, description, client, tags, picture, date) VALUES (:title, :description, :client, :tags, :picture, NOW())');

$query->bindParam(':title', $project_title, PDO::PARAM_STR);
$query->bindParam(':description', $project_description, PDO::PARAM_STR);
$query->bindParam(':client', $project_client, PDO::PARAM_STR);
$query->bindParam(':tags', $project_tags, PDO::PARAM_STR);
$query->bindParam(':picture', $project_picture, PDO::PARAM_STR);

foreach($projects as $key => $project) {

	$project_title = $project['title'];
	$project_description = $project['description'];
	$project_client = $project['client'];
	$project_tags = $project['tags'];
	$project_picture = $project['picture'];

	$query->execute();
}